<script>
	import Cookies from "js-cookie";
	import Header from '@/components/Header.vue'
	import Footer from '@/components/Footer.vue'
	import { newIndex, purchaseMember,vipList } from "_api/api";
	import QRCode from 'qrcodejs2'
	export default {
		components: {
			Header,
		    Footer
		   },
	  data(){
	    return {
		  backStyle:{
		              backgroundImage:
		                "url(" + require("@/assets/img/cardB.png") + ")",
		              backgroundRepeat: "no-repeat",
		              backgroundSize: "100%",
					  backgroundColor: "rgb(245, 245, 245)"
		   },
		   vipList:[],
		   selectCard:0,
		   type:'wechat',
		   member_id:0,
		   truePrice:0,
		   clientHeight: '',
		   showCode:false,
		   codeUrlLink:''
	    }
	  },
	   mounted() {
	          // 获取浏览器可视区域高度
	          this.clientHeight = `${document.documentElement.clientHeight}`          //document.body.clientWidth;
	          window.onresize = function temp() {
	            this.clientHeight = `${document.documentElement.clientHeight}`;
	          };
	      
			  // this.qrcodeScan();
	        },
	   watch: {
	          // 如果 `clientHeight` 发生改变，这个函数就会运行
	          clientHeight: function () {
	            this.changeFixed(this.clientHeight)
	          }
	        },
	  methods: {
		   changeFixed(clientHeight) {                        //动态修改样式
			  this.$refs.homePage.style.height = clientHeight + 'px';
  
			},
		qrcodeScan () {//生成二维码
		document.getElementById("qrcode").innerHTML = "";
		  let qrcode = new QRCode('qrcode', {  
			  width: 186,  // 二维码宽度 
			  height: 186, // 二维码高度
			  text:this.codeUrlLink
		  })  
		},
	    back() {
	      this.$router.go(-1)
	    },
		closeCode(){
			this.showCode = false
		},
		selectCardItem(t,f,y){
			 this.member_id = t
			this.selectCard = f
			this.truePrice = y
		},
		selectWechat(){
			this.type = 'wechat'
		},
		selectAlipay(){
			this.type = 'alipay'
		},
	   getDate(){
		   vipList({
		   }).then(res => {
		       if(res.code === 1){
				   this.vipList = res.data.list
				   this.member_id = this.vipList[0].id
					this.truePrice = this.vipList[0].price
		       }else{
		           this.$message.error(res.msg)
		       }
		   }).catch(err=>{
		       console.log(err)
		   })
	   },
	   goBuy(){
		   let params={
		   	  token:this.userInfo.token,
		   	  member_id:this.member_id,
		   	  type:this.type,
			  pay_type:'scan',
			  truePrice:this.truePrice
		   	}
		   purchaseMember(params).then(res => {
		       if(this.type === 'wechat' && res.result_code == "SUCCESS"){
				   this.codeUrlLink = res.code_url
				    this.qrcodeScan();
					this.showCode = true
		       }
			   if(this.type === 'alipay' && res.code == 10000){
				   
				    this.codeUrlLink = res.qr_code
					 this.qrcodeScan();
					this.showCode = true
			   }else{
		           this.$message.error(res.msg)
		       }
		   }).catch(err=>{
		       console.log(err)
		   })
		   
	   }
	},
	  created: function () {
	    this.getDate()
		if (Cookies.get("userInfo")) {
		    this.userInfo = JSON.parse(Cookies.get("userInfo"));
			console.log(this.userInfo)
		}
	  }
	}
</script>

